// The Fibonacci sequence is defined by the recurrence relation:
// What is the index of the first term in the Fibonacci sequence to contain 1000 digits?
import java.util.*;
import java.math.*;

public class P025ThousandDigitFibonacci {
  public static void main(String[] args ) {
    int index = 2;
    BigInteger number_1 = BigInteger.ONE;
    BigInteger number_2 = BigInteger.ONE;
    BigInteger next_number = BigInteger.ZERO;
    BigInteger MAX = new BigInteger("");

    while (next_number.compareTo(MAX) < 0) {
      next_number = number_1.add(number_2);
      index += 1;
      number_1 = number_2;
      number_2 = next_number;
    }

    System.out.println("The index of the first term in the Fibonacci sequence to contain 1000 digits is " + index);
  }
}
